changeSet.sql:
COMMENT ON TABLE promotions IS 'Акции и скидки';
COMMENT ON COLUMN promotions.title IS 'Название акции';
COMMENT ON COLUMN promotions.description IS 'Описание акции';
COMMENT ON COLUMN promotions.banner_image_url IS 'URL изображения для баннера акции';
COMMENT ON COLUMN promotions.discount_percent IS 'Процент скидки';
COMMENT ON COLUMN promotions.start_date IS 'Дата начала акции';
COMMENT ON COLUMN promotions.end_date IS 'Дата окончания акции';
COMMENT ON COLUMN promotions.is_active IS 'Активна ли акция';
COMMENT ON COLUMN promotions.is_featured IS 'Отображается ли на главной странице';


ALTER TABLE promotions ADD CONSTRAINT chk_promotions_discount_valid CHECK (discount_percent IS NULL OR (discount_percent >= 0.01 AND discount_percent <= 99.99));
ALTER TABLE promotions ADD CONSTRAINT chk_promotions_dates_valid CHECK (end_date > start_date);
ALTER TABLE promotions ADD CONSTRAINT chk_promotions_title_not_empty CHECK (LENGTH(TRIM(title)) > 0);
